	private int[][] inizializzaTest(int a, int b, int c, int d, int e, int f, int tipo, int val){
		int cont=10;
		int[][] matGioc = new int [8][8];
		int i = 0;
		while (i < 8) {
			int j = 0;
			while (j < 8) {
				cont++;
				if (i % 2 == 0) {
					if (cont % 2 == 0) {
						matGioc[j][i] = 0;
					} else {
						matGioc[j][i] = (cont / 2) + 1;
					}
				} else {
					if (cont % 2 != 0) {
						matGioc[j][i] = 0;
					} else {
						matGioc[j][i] = cont / 2;
					}
				}
				j = j + 1;
			}
			i = i + 1;
		}
		i = 0;
		while (i < 8) {
			int j = 0;
			while (j < 8) {
				if (matGioc[j][i] != 0) {
					if (matGioc[j][i] >= a && matGioc[j][i] <= b) {
						matGioc[j][i] = val;
					} else {
						if (matGioc[j][i] >= c && matGioc[j][i] <= d) {
							matGioc[j][i] = tipo;
						} else {
							if(matGioc[j][i] >= e && matGioc[j][i] <= f) {
								matGioc[j][i] = tipo;
							} else {
								matGioc[j][i] = 3;
							}
						}
					}
				}
				j++;
			}
			i++;
		}
		return matGioc;
	}
	
